import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const EnrollLi = styled.li`
    /* border-bottom: 1px solid #ccd; */
    & > ul {
        display: flex;
        align-items: center;
        padding: 8px 0;
        &:hover {
            background-color: #55f2;
        }
    }
    & > ul > li {
        text-align: center;
    }
    &:nth-child(2n-1) {
        background-color: #aaf2;
    }
    &:last-child {
        border-radius: 0 0 5px 5px;
    }
`;


const EnrollNumber = styled.li`
    width: 60px;
`;

const EnrollTitle = styled.li`
    flex-grow: 1;
`;

const EnrollCreator = styled.li`
    width: 100px;
`;

const EnrollCreatedDate = styled.li`
    width: 100px;
`;

const EnrollDeadline = styled.li`
    width: 100px;
`;

const EnrollStatus = styled.li`
    width: 80px;
`;

const EnrollCount = styled.li`
    width: 70px;
`;

const EnrollAccess = styled.li`
    width: 70px;
`;

const EnrollButton = styled.button`
    color: #fff;
    border: 0.5px solid #aaf;
    background-color: #aaf;
    padding: 2px 7px;
    border-radius: 2px;
    transition-duration: 0.5s;
    &:hover {
        color: #aaf;
        background-color: #fff;
    }
`;

const EnrollDetail = styled.div`
    height: 0px;
    transition: height 0.5s;
    /* padding: 20px 40px; */
    overflow: hidden;
    display: flex;
    justify-content: center;
    align-items: center;
    &.opened {
        height: 300px;
    }
`;

const StudyContent = styled.div`
    border: 1px solid #ccf;
    background-color: #fff;
    border-radius: 2px;
    width: 600px;
    height: 250px;
    margin-right: 20px;
    overflow: auto;
`;

const PreparationList = styled.ul`
    border: 1px solid #ccf;
    background-color: #fff;
    border-radius: 2px;
    width: 180px;
    height: 250px;
    margin-right: 20px;
    overflow: auto;
`;

const MemberList = styled.ul`
    border: 1px solid #ccf;
    background-color: #eef;
    border-radius: 2px;
    width: 180px;
    height: 250px;
    overflow: auto;
`;

const EnrollListItem = (props) => {
    const { allFoldDetail, setAllFoldDetail } = props;
    const [openDetail, setOpenDetail] = useState(false);

    useEffect(() => {
        if (allFoldDetail === true) {
            setOpenDetail(false);
            setAllFoldDetail(false);
        }
    }, [allFoldDetail]);

    const enrollOnClick = (e) => {
        e.stopPropagation();
        if (window.confirm('신청하시겠습니까?')) {
            alert('신청되었습니다.');
        }
    }

    return (
        <EnrollLi>
            <ul onClick={() => setOpenDetail(!openDetail)}>
                <EnrollNumber>1</EnrollNumber>
                <EnrollTitle>C 프로그래밍 스터디</EnrollTitle>
                <EnrollCreator>서원호</EnrollCreator>
                <EnrollCreatedDate>2022.07.14</EnrollCreatedDate>
                <EnrollDeadline>2022.07.24</EnrollDeadline>
                <EnrollStatus>모집중</EnrollStatus>
                <EnrollCount>0/5</EnrollCount>
                <EnrollAccess>
                    <EnrollButton onClick={e => enrollOnClick(e)}>신청</EnrollButton>
                </EnrollAccess>
            </ul>
            <EnrollDetail className={openDetail ? "opened" : null}>
                <div>
                    <span>내용</span>
                    <StudyContent></StudyContent>
                </div>
                <div>
                    <span>준비물</span>
                    <PreparationList></PreparationList>
                </div>
                <div>
                    <span>신청멤버</span>
                    <MemberList></MemberList>
                </div>
            </EnrollDetail>
        </EnrollLi>
    );
};

export default EnrollListItem;
