import React, { useState } from 'react';
import styled from 'styled-components';

const EnrollLi = styled.li`
    /* border-bottom: 1px solid #ccd; */
    & > ul {
        display: flex;
        align-items: center;
        padding: 8px 0;
        &:hover {
            background-color: #55f2;
        }
    }
    & > ul > li {
        text-align: center;
    }
    &:nth-child(2n-1) {
        background-color: #aaf2;
    }
    &:last-child {
        border-radius: 0 0 5px 5px;
    }
`;


const EnrollNumber = styled.li`
    width: 70px;
`;

const EnrollTitle = styled.li`
    flex-grow: 1;
`;

const EnrollCreator = styled.li`
    width: 100px;
`;

const EnrollCreatedDate = styled.li`
    width: 120px;
`;

const EnrollDeadline = styled.li`
    width: 120px;
`;

const EnrollStatus = styled.li`
    width: 100px;
`;

const EnrollView = styled.li`
    width: 70px;
`;

const EnrollAccess = styled.li`
    width: 70px;
`;

const EnrollButton = styled.button`
    color: #fff;
    border: 0.5px solid #aaf;
    background-color: #aaf;
    padding: 2px 7px;
    border-radius: 2px;
    transition-duration: 0.5s;
    &:hover {
        color: #aaf;
        background-color: #fff;
    }
`;

const EnrollDetail = styled.div`
    height: 0px;
    transition-duration: 0.5s;
    &.opened {
        height: 300px;
    }
`;

const EnrollListItem = () => {
    const [openDetail, setOpenDetail] = useState(false);

    return (
        <EnrollLi>
            <ul>
                <EnrollNumber onClick={() => setOpenDetail(!openDetail)}>#</EnrollNumber>
                <EnrollTitle onClick={() => setOpenDetail(!openDetail)}>제목</EnrollTitle>
                <EnrollCreator onClick={() => setOpenDetail(!openDetail)}>개설자</EnrollCreator>
                <EnrollCreatedDate onClick={() => setOpenDetail(!openDetail)}>개설일</EnrollCreatedDate>
                <EnrollDeadline onClick={() => setOpenDetail(!openDetail)}>마감일</EnrollDeadline>
                <EnrollStatus onClick={() => setOpenDetail(!openDetail)}>상태</EnrollStatus>
                <EnrollView onClick={() => setOpenDetail(!openDetail)}>조회</EnrollView>
                <EnrollAccess>
                    <EnrollButton>신청</EnrollButton>
                </EnrollAccess>
            </ul>
            <EnrollDetail className={openDetail ? "opened" : null}>

            </EnrollDetail>
        </EnrollLi>
    );
};

export default EnrollListItem;
