import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const PostArea = styled.div`
    background-color: #fff;
    width: 1200px;   
    height: 800px;
    display: flex;
    flex-direction: column;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    padding: 0 30px 30px;
`;

const PostHeader = styled.div`
    width: 100%;
    height: 70px;
    margin: 0 0 auto 0;
    display: flex;
    align-items: center;
`;

const PrevButton = styled.button`
    background-color: transparent;
    font-size: 20px;
    transition-duration: 0.5s;
    &:hover {
        transform: translateX(-2px);
        color: #aaf;
    }
`;

const PostButton = styled.button`
    margin: 0 0 0 auto;
    border: 1px solid #aaf;
    border-radius: 5px;
    background-color: #aaf;
    color: #fff;
    font-size: 16px;
    transition-duration: 0.5s;
    padding: 2px 7px;
    &:hover {
        background-color: #fff;
        color: #aaf;
    }
`;

const FormContainer = styled.div`
    height: 700px;
    padding: 20px;
`;

const Label = styled.p`
    font-size: 18px;
`;

const StudyTitle = styled.div`
    margin: 0 0 20px 0;
`;

const StudyTitleInput = styled.input`
    border: 1px solid #55f;
    border-radius: 2px;
    width: 1080px;
    padding: 5px 10px;
    font-size: 24px;
    outline: none;
`;

const StudyContent = styled.div`
    margin: 0 0 20px 0;
`;

const StudyContentInput = styled.div`
    border: 1px solid #55f;
    border-radius: 2px;
    width: 1080px;
    height: 300px;
    padding: 8px 10px;
    font-size: 16px;
    outline: none;
    overflow: auto;
`;

const StudyAdditionalContainer = styled.div`
    background-color: #faa;
    display: flex;
    width: 1080px;
    flex-grow: 1;
`;

const PostForm = () => {
    const navigate = useNavigate();

    return (
        <PostArea>
            <PostHeader>
                <PrevButton onClick={() => navigate(-1)}><i className="fa-solid fa-arrow-left"></i></PrevButton>
                <PostButton>개설하기</PostButton>
            </PostHeader>
            <FormContainer>
                <StudyTitle>
                    <Label>제목</Label>
                    <StudyTitleInput />
                </StudyTitle>
                <StudyContent>
                    <Label>내용</Label>
                    <StudyContentInput contentEditable></StudyContentInput>
                </StudyContent>
                <StudyAdditionalContainer>

                </StudyAdditionalContainer>
            </FormContainer>
        </PostArea>
    );
};

export default PostForm;
