import React from 'react';
import styled from 'styled-components';

const LoginArea = styled.div`
    background-color: #fff;
    width: 600px;   
    height: 600px;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const InputArea = styled.div`
    width: 400px;
    height: 100px;
    border: 1px solid #000;
    border-radius: 2px;
    display: flex;
    align-items: center;
`;

const IdInput = styled.input`
    width: 300px;
    height: 50px;
    border:1px solid #000;
    border-radius: 2px;
`;

const LoginLabel = styled.span`
    font-size: 24px;
    margin: 0 10px 0 0;
`;

const PwArea = styled.div`
    border:1px solid #000;
`;

const LoginForm = () => {
    return (
        <LoginArea>
            <InputArea>
                <LoginLabel>ID</LoginLabel>
                <IdInput />
            </InputArea>
            <InputArea>
            </InputArea>
        </LoginArea>
    );
};

export default LoginForm;
