import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import EnrollListItem from './EnrollListItem';

const EnrollArea = styled.div`
    background-color: #fff;
    width: 1200px;   
    height: 800px;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: 60px 30px 30px;
    position: relative;
`;

const EnrollUl = styled.ul`
    width: 100%;
    overflow: auto;
`;

const EnrollHead = styled.li`
    color: #fff;
    background-color: #aaf;
    padding: 4px 0;
    border-radius: 5px 5px 0 0;
    & ul {
        display: flex;
    }
    & > ul > li {
        text-align: center;
    }
`;

const EnrollNumber = styled.li`
    width: 60px;
`;

const EnrollTitle = styled.li`
    flex-grow: 1;
`;

const EnrollCreator = styled.li`
    width: 100px;
`;

const EnrollCreatedDate = styled.li`
    width: 100px;
`;

const EnrollDeadline = styled.li`
    width: 100px;
`;

const EnrollStatus = styled.li`
    width: 80px;
`;

const EnrollCount = styled.li`
    width: 70px;
`;

const EnrollAccess = styled.li`
    width: 70px;
`;

const PrevButton = styled.button`
    position: absolute;
    background-color: transparent;
    left: 20px;
    top: 20px;
    font-size: 20px;
    transition-duration: 0.5s;
    &:hover {
        transform: translateX(-2px);
    }
`;

const DetailAllCloseButton = styled.button`
    position: absolute;
    background-color: transparent;
    right: 20px;
    top: 20px;
    font-size: 20px;
    transition-duration: 0.5s;
    &:hover {
        transform: translateX(-2px);
    }
`;


const EnrollList = () => {
    const navigate = useNavigate();

    return (
        <EnrollArea>
            <PrevButton onClick={() => navigate(-1)}><i className="fa-solid fa-arrow-left"></i></PrevButton>
            <DetailAllCloseButton><i class="fa-solid fa-folder-minus"></i></DetailAllCloseButton>
            <EnrollUl>
                <EnrollHead>
                    <ul>
                        <EnrollNumber>#</EnrollNumber>
                        <EnrollTitle>제목</EnrollTitle>
                        <EnrollCreator>개설자</EnrollCreator>
                        <EnrollCreatedDate>개설일</EnrollCreatedDate>
                        <EnrollDeadline>마감일</EnrollDeadline>
                        <EnrollStatus>상태</EnrollStatus>
                        <EnrollCount>인원</EnrollCount>
                        <EnrollAccess>신청</EnrollAccess>
                    </ul>
                </EnrollHead>
                <EnrollListItem />
                <EnrollListItem />
                <EnrollListItem />
                <EnrollListItem />
                <EnrollListItem />
                <EnrollListItem />
            </EnrollUl>
        </EnrollArea>
    );
};

export default EnrollList;
