import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import EnrollListItem from '../enroll/EnrollListItem';


const ManageContainer = styled.div`
    background-color: #fff;
    width: 1200px;   
    height: 800px;
    display: flex;
    flex-direction: column;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    padding: 0 30px 30px;
`;

const ManageHeader = styled.div`
    width: 100%;
    height: 70px;
    margin: 0 0 auto 0;
    position: relative;
    display: flex;
    align-items: center;
`;

const SubHeader = styled.div`
    display: flex;
    align-items: center;
`;

const SubTitle = styled.h3`

`;

const EnrollUl = styled.ul`
    width: 100%;
    overflow: auto;
    height: 700px;
`;

const EnrollHead = styled.li`
    color: #fff;
    background-color: #aaf;
    padding: 4px 0;
    border-radius: 5px 5px 0 0;
    & ul {
        display: flex;
    }
    & > ul > li {
        text-align: center;
    }
`;

const EnrollNumber = styled.li`
    width: 60px;
`;

const EnrollTitle = styled.li`
    flex-grow: 1;
`;

const EnrollCreator = styled.li`
    width: 100px;
`;

const EnrollCreatedDate = styled.li`
    width: 100px;
`;

const EnrollDeadline = styled.li`
    width: 100px;
`;

const EnrollStatus = styled.li`
    width: 80px;
`;

const EnrollCount = styled.li`
    width: 70px;
`;

const EnrollAccess = styled.li`
    width: 70px;
`;

const PrevButton = styled.button`
    background-color: transparent;
    font-size: 20px;
    transition-duration: 0.5s;
    &:hover {
        transform: translateX(-2px);
        color: #aaf;
    }
`;

const StudyCountStatus = styled.h3`
    margin: 0 0 0 auto;
`

const DetailAllCloseButton = styled.button`
    margin: 0 0 0 auto;
    background-color: transparent;
    font-size: 20px;
    transition-duration: 0.5s;
    &:hover {
        transform: translateY(-2px);
        color: #aaf;
    }
`;

const StudyInfoArea = styled.div`
    background-color: #eee;
    height: 100px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 20px;
    color: #555;
`;

const ManageArea = () => {
    const [appliedFoldDetail, setAppliedFoldDetail] = useState(false);
    const [allFoldDetail, setAllFoldDetail] = useState(false);
    const navigate = useNavigate();
    const [appliedStudyCount, setAppliedStudyCount] = useState(null);
    const [studyCount, setStudyCount] = useState(null);
    const [appliedStudies, setAppliedStudies] = useState([]);
    const [studies, setStudies] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const getMemberData = () => {
            setLoading(true);
            try {
                axios.get("/api/members/my").then(response => {
                    console.log(response.data);
                }).catch(error => {
                    navigate('/');
                    alert('로그인이 필요합니다.');
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        const getNotClosedCount = () => {
            setLoading(true);
            try {
                axios.get("/api/members/my/studies/count").then(response => {
                    if (response.data.success === 0) {
                        setStudyCount(response.data.data.studyCount);
                    }
                }).catch(error => {
                    console.log(error);
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        const getMyAppliedCount = () => {
            setLoading(true);
            try {
                axios.get("/api/members/my/studies/applied/count").then(response => {
                    if (response.data.success === 0) {
                        setAppliedStudyCount(response.data.data.appliedStudyCount);
                    }
                }).catch(error => {
                    console.log(error);
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        const getStudyData = () => {
            setLoading(true);
            try {
                axios.get("/api/members/my/studies").then(response => {
                    if (response.data.success === 0) {
                        setStudies(response.data.data.studies);
                    }
                }).catch(error => {
                    console.log(error);
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        const getAppliedStudyData = () => {
            setLoading(true);
            try {
                axios.get("/api/members/my/studies/applied").then(response => {
                    if (response.data.success === 0) {
                        setAppliedStudies(response.data.data.studies);
                    }
                }).catch(error => {
                    console.log(error);
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        getMemberData();
        getNotClosedCount();
        getMyAppliedCount();
        getStudyData();
        getAppliedStudyData();
    }, []);

    if (loading) {
        return null;
    }

    return (
        <ManageContainer>
            <ManageHeader>
                <PrevButton onClick={() => navigate(-1)}><i className="fa-solid fa-arrow-left"></i></PrevButton>
            </ManageHeader>
            <SubHeader>
                    <SubTitle>내가 신청한 스터디: {appliedStudyCount}</SubTitle>
                    <DetailAllCloseButton onClick={() => setAppliedFoldDetail(true)}><i className="fa-solid fa-folder-minus"></i></DetailAllCloseButton>
                </SubHeader>
            <EnrollUl>
                <EnrollHead>
                    <ul>
                        <EnrollNumber>#</EnrollNumber>
                        <EnrollTitle>제목</EnrollTitle>
                        <EnrollCreator>개설자</EnrollCreator>
                        <EnrollCreatedDate>개설일</EnrollCreatedDate>
                        <EnrollDeadline>마감일</EnrollDeadline>
                        <EnrollStatus>상태</EnrollStatus>
                        <EnrollCount>인원</EnrollCount>
                        <EnrollAccess>모집/마감</EnrollAccess>
                    </ul>
                </EnrollHead>
                {appliedStudies.map((study, index) => (
                    <EnrollListItem key={study.id} allFoldDetail={appliedFoldDetail} setAllFoldDetail={setAppliedFoldDetail} study={study} index={index+1} status="enroll"/>
                ))}
                {!appliedStudyCount && <StudyInfoArea>
                    <p>신청한 스터디가 없습니다.</p>
                </StudyInfoArea>}
            </EnrollUl>
            <hr />
            <SubTitle>내가 개설한 스터디: {studyCount}</SubTitle>
            <EnrollUl>
                <SubHeader>
                    <DetailAllCloseButton onClick={() => setAllFoldDetail(true)}><i className="fa-solid fa-folder-minus"></i></DetailAllCloseButton>
                </SubHeader>
                <EnrollHead>
                    <ul>
                        <EnrollNumber>#</EnrollNumber>
                        <EnrollTitle>제목</EnrollTitle>
                        <EnrollCreator>개설자</EnrollCreator>
                        <EnrollCreatedDate>개설일</EnrollCreatedDate>
                        <EnrollDeadline>마감일</EnrollDeadline>
                        <EnrollStatus>상태</EnrollStatus>
                        <EnrollCount>인원</EnrollCount>
                        <EnrollAccess>모집/마감</EnrollAccess>
                    </ul>
                </EnrollHead>
                {studies.map((study, index) => (
                    <EnrollListItem key={study.id} allFoldDetail={allFoldDetail} setAllFoldDetail={setAllFoldDetail} study={study} index={index+1} status="manage"/>
                ))}
                {!studyCount && <StudyInfoArea>
                    <p>개설한 스터디가 없습니다.</p>
                </StudyInfoArea>}
            </EnrollUl>
        </ManageContainer>
    );
};

export default ManageArea;
