import styled from 'styled-components';

const LoginArea = styled.div`
    background-color: #fff;
    width: 600px;   
    height: 600px;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const IdArea = styled.div`
    width: 400px;
    border: 1px solid #000;
    border-radius: 2px;
`;

const IdInput = styled.input`
    width: 300px;
    height: 50px;
    border:1px solid #000;
    border-radius: 2px;
`;

const LoginLabel = styled.span`

`;

const PwArea = styled.div`
    border:1px solid #000;
`;

const LoginForm = () => {
    return (
        <LoginArea>
            <LoginLabel>ID</LoginLabel>
            <IdArea>
                <IdInput />
            </IdArea>
            <PwArea>

            </PwArea>
        </LoginArea>
    );
};

export default LoginForm;
