import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const Label = styled.span`
    color: #bbf;
    font-size: 20px;
`;

const NavContainer = styled.ul`
    background-color: #33a;
    width: 100%;
    height: 560px;
    & > li {
        height: 80px;
        position: relative;
        display: flex;
        justify-content: center;
        align-items: center;
        transition-duration: 0.3s;
        &::after{
            content: '';
            position: absolute;
            border-style: solid;
            border-width: 20px 0 20px 20px;
            border-color: transparent #55a;
            display: none;
            width: 0;
            z-index: 1;
            top: 20px;
            right: -20px;
        }
        &:hover {
            cursor: pointer;
            background-color: #55a;
            &::after {
                display: block;
            }
            & ${Label} {
                color: #fff;
            }
        }
        &:not(:last-child) {
            border-bottom: 1px solid #aaf;
        }
    }
`;

const AdminNav = () => {
    const [selected, setSelected] = useState('studyManage');

    useEffect(() => {
        console.log(selected);
    }, [selected]);

    return (
        <NavContainer>
            <li onClick={(e) => e.target.class.toggle("selected")} className="studyManage"><Label>스터디 관리</Label></li>
            <li onClick={(e) => setSelected(e.target.value)} value="log"><Label>활동 로그</Label></li>
            <li><Label>일반 사용자 관리</Label></li>
            <li><Label>관리자 설정</Label></li>

            <li><Label>스터디 관리</Label></li>
            <li><Label>스터디 관리</Label></li>
            <li><Label>스터디 관리</Label></li>
        </NavContainer>
    );
};

export default AdminNav;
