const EnrollList = () => {
    const [allFoldDetail, setAllFoldDetail] = useState(false);
    const navigate = useNavigate();
    const [notClosedCount, setNotClosedCount] = useState(null);
    const [studies, setStudies] = useState([]);
    const [viewProgressStudy, setViewProgressStudy] = useState(false);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const getMemberData = () => {
            setLoading(true);
            try {
                axios.get("/api/members/my").then(response => {
                    console.log(response.data);
                }).catch(error => {
                    navigate('/');
                    alert('로그인이 필요합니다.');
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        const getNotClosedCount = () => {
            setLoading(true);
            try {
                axios.get("/api/studies/count").then(response => {
                    if (response.data.success === 0) {
                        setNotClosedCount(response.data.data.notClosedCount);
                    }
                }).catch(error => {
                    console.log(error);
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        const getStudyData = () => {
            setLoading(true);
            try {
                axios.get("/api/studies").then(response => {
                    if (response.data.success === 0) {
                        setStudies(response.data.data.studies);
                    }
                }).catch(error => {
                    console.log(error);
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        }
        getMemberData();
        getNotClosedCount();
        getStudyData();
    }, []);

    if (loading) {
        return null;
    }

    return (
        <EnrollArea>
            <EnrollHeader>
                <PrevButton onClick={() => navigate(-1)}><i className="fa-solid fa-arrow-left"></i></PrevButton>
                <HeaderLabel>모집 중인 스터디만 보기</HeaderLabel>
                <CheckButton type="checkbox" className="switch"></CheckButton>
                <StudyCountStatus>현재 모집 중인 스터디: {notClosedCount}</StudyCountStatus>
                <DetailAllCloseButton onClick={() => setAllFoldDetail(true)}><i className="fa-solid fa-folder-minus"></i></DetailAllCloseButton>
            </EnrollHeader>
            <EnrollUl>
                <EnrollHead>
                    <ul>
                        <EnrollNumber>#</EnrollNumber>
                        <EnrollTitle>제목</EnrollTitle>
                        <EnrollCreator>개설자</EnrollCreator>
                        <EnrollCreatedDate>개설일</EnrollCreatedDate>
                        <EnrollDeadline>마감일</EnrollDeadline>
                        <EnrollStatus>상태</EnrollStatus>
                        <EnrollCount>인원</EnrollCount>
                        <EnrollAccess>신청</EnrollAccess>
                    </ul>
                </EnrollHead>
                {studies.map((study, index) => (
                    <EnrollListItem key={study.id} allFoldDetail={allFoldDetail} setAllFoldDetail={setAllFoldDetail} study={study} index={index+1} status="enroll" />
                ))}
            </EnrollUl>
        </EnrollArea>
    );
};

export default EnrollList;
