import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const MenuSection = styled.div`
    background-color: #ccf;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const MenuArea = styled.div`
    background-color: #fff;
    width: 600px;   
    height: 450px;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    padding: 40px;
    position: relative;
`;

const MenuTitle = styled.h1`
    text-align: center;
    margin: 0 0 20px 0;
    font-weight: normal;
`;

const CardArea = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
`;

const MenuCard = styled.button`
    width: 200px;
    height: 200px;
    background-color: #aaf;
    color: #fff;
    font-size: 24px;
    box-shadow: 3px 3px 10px -1px #0004;
    border-radius: 5px;
    transition-duration: 0.5s;
    margin: 30px;
    &:hover {
        transform: translateY(-2px);
    }
`;

const LogoutButton = styled.button`
    position: absolute;
    background-color: transparent;
    top: 20px;
    right: 20px;
    font-size: 20px;
`;

const MenuMain = () => {
    const navigate = useNavigate();

    return (
        <MenuSection>
            <MenuArea>
                <LogoutButton><i className="fa-solid fa-arrow-right-from-bracket"></i></LogoutButton>
                <MenuTitle>안녕하세요 000님</MenuTitle>
                <CardArea>
                    <MenuCard onClick={() => navigate('/enroll')}>스터디 신청</MenuCard>
                    <MenuCard>스터디 등록</MenuCard>
                </CardArea>
            </MenuArea>
        </MenuSection>
    );
};

export default MenuMain;
