import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import ApplyImg from '../../images/apply.png';

const MenuSection = styled.div`
    background-color: #ccf;
    height: 100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const MenuArea = styled.div`
    background-color: #fff;
    width: 650px;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    padding: 40px;
    position: relative;
`;

const MenuTitle = styled.h1`
    text-align: center;
    margin: 0 0 20px 0;
    font-weight: normal;
`;

const CardArea = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
`;

const MenuCard = styled.button`
    width: 200px;
    height: 180px;
    background-color: #aaf;
    color: #fff;
    font-size: 24px;
    box-shadow: 3px 3px 10px -1px #0004;
    border-radius: 5px;
    transition-duration: 0.5s;
    margin: 30px;
    &:hover {
        transform: translateY(-2px);
    }
`;

const LogoutButton = styled.button`
    position: absolute;
    background-color: transparent;
    top: 20px;
    right: 20px;
    font-size: 20px;
`;

const MenuMain = () => {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [member, setMember] = useState({});

    useEffect(() => {
        const getMemberData = () => {
            setLoading(true);
            try {
                axios.get("/api/members/my").then(response => {
                    if (response.data.success === 0) {
                        setMember(response.data.data.member);
                    }
                }).catch(error => {
                    navigate('/');
                    alert('로그인이 필요합니다.');
                });
            } catch (e) {
                console.log(e);
            }
            setLoading(false);
        };
        getMemberData();
    }, []);

    const logout = () => {
        setLoading(true);
        try {
            axios.get("/api/members/logout").then(response => {
                if (response.data.success === 0) {
                    alert('로그아웃 되었습니다.');
                    navigate('/');
                }
            }).catch(error => {
                console.log(error);
            });
        } catch (e) {
            console.log(e);
        }
        setLoading(false);
    };

    if (loading) {
        return null;
    }

    return (
        <MenuSection>
            <MenuArea>
                <LogoutButton onClick={() => logout()}><i className="fa-solid fa-arrow-right-from-bracket"></i></LogoutButton>
                <MenuTitle>안녕하세요 {member.memberName}님</MenuTitle>
                <CardArea>
                    <MenuCard className="apply" onClick={() => navigate('/enroll')}><img style={{width: '30%'}} src={ApplyImg} /> 스터디 신청</MenuCard>
                    <MenuCard onClick={() => navigate('/post')}>스터디 개설</MenuCard>
                    <MenuCard onClick={() => navigate('/')}>스터디 관리</MenuCard>
                    <MenuCard onClick={() => navigate('/')}>커뮤니티</MenuCard>
                </CardArea>
            </MenuArea>
        </MenuSection>
    );
};

export default MenuMain;
