import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const LoginArea = styled.div`
    background-color: #fff;
    width: 600px;   
    height: 450px;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
`;

const InputArea = styled.div`
    height: 80px;
    border-radius: 2px;
    display: grid;
    grid-template-columns: 50px 300px;
    grid-template-rows: 50px 30px;
    justify-content: center;
    align-items: center;
`;

const Input = styled.input`
    width: 300px;
    height: 50px;
    border:1px solid #000;
    border-radius: 2px;
    padding: 10px;
    outline: none;
`;

const LoginLabel = styled.span`
    font-size: 20px;
    text-align: center;
    width: 50px;
`;

const ErrorMessage = styled.span`
    color: red;
    grid-area: 2 / 2 / 2 / 2;
`;

const LoginButton = styled.button`
    width: 300px;
    height: 60px;
    background-color: #55f;
    border-radius: 2px;
    color: #fff;
    font-size: 20px;
    transition-duration: 0.2s;
    &:hover {
        border: 1px solid #55f;
        background-color: #fff;
        color: #55f;
    }
`;

const AccessArea = styled.div`
    width: 300px;
    height: 30px;
    margin: 10px 0 0 0;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const SignupButton = styled.button`
    background-color: transparent;
    color: #555;
    margin: 0 10px;
    &:hover {
        color: #55f;
    }
`;

const LoginForm = () => {
    const navigate = useNavigate();

    return (
        <LoginArea>
            <InputArea>
                <LoginLabel><i className="fa-solid fa-user"></i></LoginLabel>
                <Input placeholder='아이디를 입력해주세요' />
                <ErrorMessage>123</ErrorMessage>
            </InputArea>
            <InputArea>
                <LoginLabel><i className="fa-solid fa-key"></i></LoginLabel>
                <Input type='password' placeholder='비밀번호를 입력해주세요' />
                <ErrorMessage>123</ErrorMessage>
            </InputArea>
            <LoginButton>로그인</LoginButton>
            <AccessArea>
                <span>아직 회원이 아니신가요? </span>
                <SignupButton onClick={() => navigate('/signup')}>회원가입</SignupButton>
            </AccessArea>
        </LoginArea>
    );
};

export default LoginForm;
