import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const PostArea = styled.div`
    background-color: #fff;
    width: 1200px;   
    height: 900px;
    display: flex;
    flex-direction: column;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    padding: 0 30px 30px;
`;

const PostHeader = styled.div`
    width: 100%;
    height: 70px;
    display: flex;
    align-items: center;
`;

const PrevButton = styled.button`
    background-color: transparent;
    font-size: 20px;
    transition-duration: 0.5s;
    &:hover {
        transform: translateX(-2px);
        color: #aaf;
    }
`;

const PostButton = styled.button`
    margin: 0 0 0 auto;
    border: 1px solid #aaf;
    border-radius: 5px;
    background-color: #aaf;
    color: #fff;
    font-size: 16px;
    transition-duration: 0.5s;
    padding: 2px 7px;
    &:hover {
        background-color: #fff;
        color: #aaf;
    }
`;

const FormContainer = styled.div`
    height: 800px;
    padding: 20px;
`;

const Label = styled.p`
    font-size: 18px;
`;

const SubLabel = styled.p`
    font-size: 14px;
`;

const StudyTitle = styled.div`
    margin: 0 0 20px 0;
`;

const StudyTitleInput = styled.input`
    border: 1px solid #55f;
    border-radius: 2px;
    width: 100%;
    padding: 5px 10px;
    font-size: 24px;
    outline: none;
`;

const StudyContent = styled.div`
    margin: 0 0 20px 0;
`;

const StudyContentInput = styled.div`
    border: 1px solid #55f;
    border-radius: 2px;
    width: 100%;
    height: 300px;
    padding: 8px 10px;
    font-size: 16px;
    outline: none;
    overflow: auto;
`;

const StudyAdditionalContainer = styled.div`
    display: flex;
    width: 100%;
    height: 300px;
`;

const StudyPreparation = styled.div`
    width: 33%;
    margin: 0 3% 0 0;
`;

const StudyPreparationInput = styled.input`
    border: 1px solid #aaf;
    border-radius: 2px;
    width: 100%;
    padding: 5px 10px;
    outline: none;
    margin: 10px 0;
`;

const StudyPreparationItemContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    border-right: 1px solid #aaf;
    border-left: 1px solid #aaf;
    padding: 10px 10px 5px;
`;

const StudyPreparationItem = styled.div`
    border: 1px solid #aaf;
    border-radius: 2px;
    background-color: #55f2;
    padding: 2px 6px;
    margin: 0 10px 10px 0;
`;

const StudyRequired = styled.div`
    width: 64%;
`;

const DeadlineContainer = styled.div`

`;

const DeadlineSelector = styled.select`
    border: 1px solid #78a;
    border-radius: 2px;
    cursor: pointer;
    margin-right: 10px;
`;

const MemberCountContainer = styled.div`

`;

const RestrictionContainer = styled.div`

`;


const PostForm = () => {
    const navigate = useNavigate();
    const [preparationList, setPreparationList] = useState([]);
    const today = new Date();
    const currentDate = new Date(today.getFullYear(), today.getMonth(), 0).getDate();
    const [years, setYears] = useState();
    const [months, setMonths] = useState();
    const [dates, setDates] = useState();
    const [post, setPost] = useState({
        deadlineDate: String(today.getFullYear()) + String(today.getMonth).padStart(2, '0') + String(today.getDate).padStart(2, '0') + " 00:00:00"
    });
    const year = useRef();
    const month = useRef();
    const date = useRef();

    useEffect(() => {
        let yearList = [];
        let monthList = [];
        let dateList = [];
        for (let y = today.getFullYear(); y < today.getFullYear() + 5; y++) {
            yearList.push(y);
        }
        for (let m = 1; m <= 12; m++) {
            monthList.push(m);
        }
        for (let d = 1; d <= currentDate; d++) {
            dateList.push(d);
        }
        setYears(yearList);
        setMonths(monthList);
        setDates(dateList);
    }, [])

    useEffect(() => {
        if (year.current && month.current) {
            let date;
            let dateList = [];
            date = new Date(year.current.value, month.current.value, 0).getDate();
            for (let d = 1; d <= date; d++) {
                dateList.push(d);
            }
            setDates(dateList);
        }
    }, [post.deadlineDate]);

    const deletePreparation = (deleteIndex) => {
        setPreparationList(preparationList.filter((preparation, index) => index !== deleteIndex));
    };

    const addPreparation = (e) => {
        if (e.keyCode === 13) {
            setPreparationList([...preparationList, e.target.value])
            e.target.value = "";
        }
    }

    const onChangeDeadline = (e) => {
        const nextPost = {
            ...post,
            deadlineDate: year.current.value + "." + month.current.value + "." + date.current.value + " 00:00:00"
        };
        setPost(nextPost);
    };

    if (!post) {
        return null;
    }

    return (
        <PostArea>
            <PostHeader>
                <PrevButton onClick={() => navigate(-1)}><i className="fa-solid fa-arrow-left"></i></PrevButton>
                <PostButton>개설하기</PostButton>
            </PostHeader>
            <FormContainer>
                <StudyTitle>
                    <Label>제목</Label>
                    <StudyTitleInput />
                </StudyTitle>
                <StudyContent>
                    <Label>내용</Label>
                    <StudyContentInput contentEditable></StudyContentInput>
                </StudyContent>
                <StudyAdditionalContainer>
                    <StudyPreparation>
                        <Label>준비물</Label>
                        <StudyPreparationItemContainer>
                            {
                                preparationList.map((preparation, index) => (
                                    <StudyPreparationItem key={index}>{preparation} <span style={{ cursor: 'pointer'}} onClick={() => deletePreparation(index)}><i className="fa-solid fa-xmark"></i></span></StudyPreparationItem>
                                ))
                            }
                        </StudyPreparationItemContainer>
                        <StudyPreparationInput onKeyDown={e => addPreparation(e)} placeholder="준비물을 입력해주세요"/>
                    </StudyPreparation>
                    <StudyRequired>
                        <Label>설정</Label>
                        <DeadlineContainer>
                            <SubLabel>마감일</SubLabel>
                            {post.deadlineDate && <DeadlineSelector defaultValue={post.deadlineDate.split('.')[0]} onChange={e => onChangeDeadline(e)}>
                                {years.map((year, index) => (<option key={index} value={year}>{year}년</option>))}
                            </DeadlineSelector>}
                            {post.deadlineDate && <DeadlineSelector defaultValue={post.deadlineDate.split('.')[1]} onChange={e => onChangeDeadline(e)}>
                                {months.map((month, index) => (<option key={index} value={String(month).padStart(2, '0')}>{month}월</option>))}
                            </DeadlineSelector>}
                            {post.deadlineDate && <DeadlineSelector defaultValue={post.deadlineDate.split('.')[2].split(' ')[0]} onChange={e => onChangeDeadline(e)}>
                                {dates.map((date, index) => (<option key={index} value={String(date).padStart(2, '0')}>{date}일</option>))}
                            </DeadlineSelector>}
                        </DeadlineContainer>
                        <MemberCountContainer>
                            <SubLabel>모집인원</SubLabel>
                        </MemberCountContainer>
                        <RestrictionContainer>
                            <SubLabel>제한사항</SubLabel>
                        </RestrictionContainer>
                    </StudyRequired>
                </StudyAdditionalContainer>
            </FormContainer>
        </PostArea>
    );
};

export default PostForm;
