import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import EnrollListItem from './EnrollListItem';

const EnrollArea = styled.div`
    background-color: #fff;
    width: 1200px;   
    height: 800px;
    display: flex;
    flex-direction: column;
    border-radius: 2px;
    box-shadow: 0px 5px 10px 1px #0004;
    padding: 0 30px 30px;
`;

const EnrollHeader = styled.div`
    width: 100%;
    height: 70px;
    margin: 0 0 auto 0;
    position: relative;
    display: flex;
    align-items: center;
`;

const EnrollUl = styled.ul`
    width: 100%;
    overflow: auto;
    height: 700px;
`;

const EnrollHead = styled.li`
    color: #fff;
    background-color: #aaf;
    padding: 4px 0;
    border-radius: 5px 5px 0 0;
    & ul {
        display: flex;
    }
    & > ul > li {
        text-align: center;
    }
`;

const EnrollNumber = styled.li`
    width: 60px;
`;

const EnrollTitle = styled.li`
    flex-grow: 1;
`;

const EnrollCreator = styled.li`
    width: 100px;
`;

const EnrollCreatedDate = styled.li`
    width: 100px;
`;

const EnrollDeadline = styled.li`
    width: 100px;
`;

const EnrollStatus = styled.li`
    width: 80px;
`;

const EnrollCount = styled.li`
    width: 70px;
`;

const EnrollAccess = styled.li`
    width: 70px;
`;

const PrevButton = styled.button`
    background-color: transparent;
    font-size: 20px;
    transition-duration: 0.5s;
    &:hover {
        transform: translateX(-2px);
    }
`;

const StudyCountStatus = styled.h3`
    margin: 0 0 0 auto;
`

const DetailAllCloseButton = styled.button`
    margin: 0 0 0 20px;
    background-color: transparent;
    font-size: 20px;
    transition-duration: 0.5s;
    &:hover {
        transform: translateY(-2px);
    }
`;


const EnrollList = () => {
    const [allFoldDetail, setAllFoldDetail] = useState(false);
    const navigate = useNavigate();

    return (
        <EnrollArea>
            <EnrollHeader>
                <PrevButton onClick={() => navigate(-1)}><i className="fa-solid fa-arrow-left"></i></PrevButton>
                <StudyCountStatus>현재 스터디: 5</StudyCountStatus>
                <DetailAllCloseButton onClick={() => setAllFoldDetail(true)}><i class="fa-solid fa-folder-minus"></i></DetailAllCloseButton>
            </EnrollHeader>
            <EnrollUl>
                <EnrollHead>
                    <ul>
                        <EnrollNumber>#</EnrollNumber>
                        <EnrollTitle>제목</EnrollTitle>
                        <EnrollCreator>개설자</EnrollCreator>
                        <EnrollCreatedDate>개설일</EnrollCreatedDate>
                        <EnrollDeadline>마감일</EnrollDeadline>
                        <EnrollStatus>상태</EnrollStatus>
                        <EnrollCount>인원</EnrollCount>
                        <EnrollAccess>신청</EnrollAccess>
                    </ul>
                </EnrollHead>
                <EnrollListItem allFoldDetail={allFoldDetail} setAllFoldDetail={setAllFoldDetail} />
                <EnrollListItem allFoldDetail={allFoldDetail} setAllFoldDetail={setAllFoldDetail} />
                <EnrollListItem allFoldDetail={allFoldDetail} setAllFoldDetail={setAllFoldDetail} />
                <EnrollListItem allFoldDetail={allFoldDetail} setAllFoldDetail={setAllFoldDetail} />
                <EnrollListItem allFoldDetail={allFoldDetail} setAllFoldDetail={setAllFoldDetail} />
                <EnrollListItem allFoldDetail={allFoldDetail} setAllFoldDetail={setAllFoldDetail} />
            </EnrollUl>
        </EnrollArea>
    );
};

export default EnrollList;
